import React from 'react';
import { View, StyleSheet } from 'react-native';
import { useNavigation } from './NavigationContext';
import { useAuth } from '../hooks/useAuth';
import TabBar from './TabBar';

// Import all screens
import LoginScreen from '../screens/LoginScreen';
import RegisterScreen from '../screens/RegisterScreen';
import HomeScreen from '../screens/HomeScreen';
import PrayerRequestsScreen from '../screens/PrayerRequestsScreen';
import RequestDetailsScreen from '../screens/RequestDetailsScreen';
import GroupsScreen from '../screens/GroupsScreen';
import GroupDetailsScreen from '../screens/GroupDetailsScreen';
import ExploreGroupsScreen from '../screens/ExploreGroupsScreen';
import ProfileScreen from '../screens/ProfileScreen';
import SettingsScreen from '../screens/SettingsScreen';
import OrganizationsScreen from '../screens/OrganizationsScreen';
import OrganizationDetailsScreen from '../screens/OrganizationDetailsScreen';
import OrganizationOnboardingScreen from '../screens/OrganizationOnboardingScreen';
import MeetingsScreen from '../screens/MeetingsScreen';
import MeetingDetailsScreen from '../screens/MeetingDetailsScreen';
import ForgotPasswordScreen from '../screens/ForgotPasswordScreen';
import ResetPasswordScreen from '../screens/ResetPasswordScreen';

const Navigator: React.FC = () => {
  const { currentScreen, isTabScreen } = useNavigation();
  const { user } = useAuth();

  // Conditionally render screens based on authentication state
  if (!user) {
    // Auth flow screens
    switch (currentScreen) {
      case 'Register':
        return <RegisterScreen />;
      case 'ForgotPassword':
        return <ForgotPasswordScreen />;
      case 'ResetPassword':
        return <ResetPasswordScreen />;
      case 'OrganizationOnboarding':
        return <OrganizationOnboardingScreen />;
      case 'Login':
      default:
        return <LoginScreen />;
    }
  }

  // Main app screens with the tab bar
  return (
    <View style={styles.container}>
      <View style={styles.content}>
        {/* Render screen based on currentScreen */}
        {currentScreen === 'Home' && <HomeScreen />}
        {currentScreen === 'PrayerRequests' && <PrayerRequestsScreen />}
        {currentScreen === 'RequestDetails' && <RequestDetailsScreen />}
        {currentScreen === 'Groups' && <GroupsScreen />}
        {currentScreen === 'GroupDetails' && <GroupDetailsScreen />}
        {currentScreen === 'ExploreGroups' && <ExploreGroupsScreen />}
        {currentScreen === 'Profile' && <ProfileScreen />}
        {currentScreen === 'Settings' && <SettingsScreen />}
        {currentScreen === 'Organizations' && <OrganizationsScreen />}
        {currentScreen === 'OrganizationDetails' && <OrganizationDetailsScreen />}
        {currentScreen === 'Meetings' && <MeetingsScreen />}
        {currentScreen === 'MeetingDetails' && <MeetingDetailsScreen />}
      </View>

      {/* Show TabBar only for main tab screens */}
      {isTabScreen(currentScreen) && (
        <TabBar activeScreen={currentScreen} />
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  content: {
    flex: 1,
  },
});

export default Navigator;
