import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  StyleSheet,
  ScrollView,
  Alert,
  Platform,
} from 'react-native';
import { useMutation } from '@tanstack/react-query';
import { useNavigation, useRoute } from '@react-navigation/native';
import { resetPasswordSchema, ResetPasswordData } from '../shared/schema';
import { apiRequest } from '../api/queryClient';
import { ZodError } from 'zod';

export default function ResetPasswordScreen() {
  const navigation = useNavigation();
  const route = useRoute();

  // Get token from route params
  const token = route.params && (route.params as any).token;

  // Form state
  const [formData, setFormData] = useState<ResetPasswordData>({
    token: token || '',
    password: '',
    confirmPassword: '',
  });

  // Show/hide password state
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);

  // Validation errors
  const [errors, setErrors] = useState<Record<string, string>>({});

  // Success state
  const [isSuccess, setIsSuccess] = useState(false);

  // Update token if it changes in the route
  useEffect(() => {
    if (token) {
      setFormData(prev => ({ ...prev, token }));
    }
  }, [token]);

  const resetPasswordMutation = useMutation({
    mutationFn: async (data: ResetPasswordData) => {
      // Validate with Zod schema
      try {
        resetPasswordSchema.parse(data);
      } catch (error) {
        if (error instanceof ZodError) {
          const formattedErrors: Record<string, string> = {};
          error.errors.forEach((err) => {
            if (err.path.length > 0) {
              formattedErrors[err.path[0]] = err.message;
            }
          });
          setErrors(formattedErrors);
          throw new Error('Validation failed');
        }
        throw error;
      }

      const response = await apiRequest('POST', '/api/auth/reset-password', data);

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.message || 'Failed to reset password');
      }

      return response.json();
    },
    onSuccess: () => {
      setIsSuccess(true);
    },
    onError: (error: Error) => {
      if (error.message !== 'Validation failed') {
        Alert.alert('Error', error.message);
      }
    },
  });

  const handleInputChange = (field: keyof ResetPasswordData, value: string) => {
    setFormData({
      ...formData,
      [field]: value,
    });

    // Clear errors for this field when user types
    if (errors[field]) {
      setErrors({
        ...errors,
        [field]: '',
      });
    }
  };

  const handleSubmit = () => {
    // Reset errors
    setErrors({});

    // Submit the form
    resetPasswordMutation.mutate(formData);
  };

  // Show success screen
  if (isSuccess) {
    return (
      <ScrollView style={styles.container}>
        <View style={styles.successContainer}>
          <Text style={styles.successTitle}>Password Reset Successful</Text>
          <Text style={styles.successText}>
            Your password has been successfully reset. You can now log in with your new password.
          </Text>

          <TouchableOpacity
            style={styles.returnButton}
            onPress={() => navigation.navigate('Login' as never)}
          >
            <Text style={styles.returnButtonText}>Go to Login</Text>
          </TouchableOpacity>
        </View>
      </ScrollView>
    );
  }

  return (
    <ScrollView style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.title}>Reset Password</Text>
      </View>

      <View style={styles.formContainer}>
        <Text style={styles.instructions}>
          Please enter your new password below.
        </Text>

        {!token && (
          <View style={styles.fieldContainer}>
            <Text style={styles.label}>Reset Token</Text>
            <TextInput
              style={styles.input}
              value={formData.token}
              onChangeText={(value) => handleInputChange('token', value)}
              placeholder="Enter the reset token from your email"
              autoCapitalize="none"
            />
            {errors.token && (
              <Text style={styles.errorText}>{errors.token}</Text>
            )}
          </View>
        )}

        <View style={styles.fieldContainer}>
          <Text style={styles.label}>New Password</Text>
          <View style={styles.passwordContainer}>
            <TextInput
              style={styles.passwordInput}
              value={formData.password}
              onChangeText={(value) => handleInputChange('password', value)}
              placeholder="Enter your new password"
              secureTextEntry={!showPassword}
              autoCapitalize="none"
            />
            <TouchableOpacity
              style={styles.visibilityToggle}
              onPress={() => setShowPassword(!showPassword)}
            >
              <Text style={styles.visibilityToggleText}>
                {showPassword ? 'Hide' : 'Show'}
              </Text>
            </TouchableOpacity>
          </View>
          {errors.password && (
            <Text style={styles.errorText}>{errors.password}</Text>
          )}
        </View>

        <View style={styles.fieldContainer}>
          <Text style={styles.label}>Confirm New Password</Text>
          <View style={styles.passwordContainer}>
            <TextInput
              style={styles.passwordInput}
              value={formData.confirmPassword}
              onChangeText={(value) => handleInputChange('confirmPassword', value)}
              placeholder="Confirm your new password"
              secureTextEntry={!showConfirmPassword}
              autoCapitalize="none"
            />
            <TouchableOpacity
              style={styles.visibilityToggle}
              onPress={() => setShowConfirmPassword(!showConfirmPassword)}
            >
              <Text style={styles.visibilityToggleText}>
                {showConfirmPassword ? 'Hide' : 'Show'}
              </Text>
            </TouchableOpacity>
          </View>
          {errors.confirmPassword && (
            <Text style={styles.errorText}>{errors.confirmPassword}</Text>
          )}
        </View>

        <TouchableOpacity
          style={styles.submitButton}
          onPress={handleSubmit}
          disabled={resetPasswordMutation.isPending}
        >
          <Text style={styles.submitButtonText}>
            {resetPasswordMutation.isPending ? 'Resetting...' : 'Reset Password'}
          </Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={styles.returnToLoginButton}
          onPress={() => navigation.navigate('Login' as never)}
        >
          <Text style={styles.returnToLoginButtonText}>Return to Login</Text>
        </TouchableOpacity>
      </View>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f8f9fa',
  },
  header: {
    padding: 16,
    backgroundColor: '#ffffff',
    borderBottomWidth: 1,
    borderBottomColor: '#e1e4e8',
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
    color: '#333333',
  },
  formContainer: {
    padding: 16,
  },
  instructions: {
    fontSize: 16,
    lineHeight: 24,
    color: '#4a5568',
    marginBottom: 16,
  },
  fieldContainer: {
    marginBottom: 16,
  },
  label: {
    fontSize: 16,
    fontWeight: '500',
    marginBottom: 8,
    color: '#4a5568',
  },
  input: {
    backgroundColor: '#ffffff',
    borderWidth: 1,
    borderColor: '#e1e4e8',
    borderRadius: 8,
    padding: Platform.OS === 'ios' ? 12 : 10,
    fontSize: 16,
    color: '#333333',
  },
  passwordContainer: {
    flexDirection: 'row',
    backgroundColor: '#ffffff',
    borderWidth: 1,
    borderColor: '#e1e4e8',
    borderRadius: 8,
  },
  passwordInput: {
    flex: 1,
    padding: Platform.OS === 'ios' ? 12 : 10,
    fontSize: 16,
    color: '#333333',
  },
  visibilityToggle: {
    justifyContent: 'center',
    paddingHorizontal: 12,
  },
  visibilityToggleText: {
    color: '#4299e1',
    fontWeight: '500',
  },
  errorText: {
    color: '#e53e3e',
    marginTop: 4,
    fontSize: 14,
  },
  submitButton: {
    backgroundColor: '#4299e1',
    borderRadius: 8,
    padding: 16,
    alignItems: 'center',
    marginTop: 16,
  },
  submitButtonText: {
    color: '#ffffff',
    fontSize: 16,
    fontWeight: '600',
  },
  returnToLoginButton: {
    padding: 16,
    alignItems: 'center',
    marginTop: 8,
  },
  returnToLoginButtonText: {
    color: '#4299e1',
    fontSize: 16,
    fontWeight: '500',
  },
  successContainer: {
    padding: 24,
    alignItems: 'center',
    justifyContent: 'center',
    minHeight: 400,
  },
  successTitle: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#38a169',
    marginBottom: 16,
    textAlign: 'center',
  },
  successText: {
    fontSize: 16,
    lineHeight: 24,
    color: '#4a5568',
    textAlign: 'center',
    marginBottom: 16,
  },
  returnButton: {
    backgroundColor: '#4299e1',
    borderRadius: 8,
    padding: 16,
    alignItems: 'center',
    width: '80%',
    marginTop: 16,
  },
  returnButtonText: {
    color: '#ffffff',
    fontSize: 16,
    fontWeight: '600',
  },
});
