import React, { useState } from 'react';
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  StyleSheet,
  ScrollView,
  Alert,
  Platform,
} from 'react-native';
import { useMutation } from '@tanstack/react-query';
import { useNavigation } from '@react-navigation/native';
import { forgotPasswordSchema, ForgotPasswordData } from '../shared/schema';
import { apiRequest } from '../api/queryClient';
import { ZodError } from 'zod';

export default function ForgotPasswordScreen() {
  const navigation = useNavigation();

  // Form state
  const [email, setEmail] = useState('');

  // Validation errors
  const [errors, setErrors] = useState<Record<string, string>>({});

  // Success state
  const [isSuccess, setIsSuccess] = useState(false);

  const forgotPasswordMutation = useMutation({
    mutationFn: async (data: ForgotPasswordData) => {
      // Validate with Zod schema
      try {
        forgotPasswordSchema.parse(data);
      } catch (error) {
        if (error instanceof ZodError) {
          const formattedErrors: Record<string, string> = {};
          error.errors.forEach((err) => {
            if (err.path.length > 0) {
              formattedErrors[err.path[0]] = err.message;
            }
          });
          setErrors(formattedErrors);
          throw new Error('Validation failed');
        }
        throw error;
      }

      const response = await apiRequest('POST', '/api/auth/forgot-password', data);

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.message || 'Failed to process request');
      }

      return response.json();
    },
    onSuccess: () => {
      setIsSuccess(true);
    },
    onError: (error: Error) => {
      if (error.message !== 'Validation failed') {
        Alert.alert('Error', error.message);
      }
    },
  });

  const handleInputChange = (value: string) => {
    setEmail(value);

    // Clear errors for this field when user types
    if (errors.email) {
      setErrors({
        ...errors,
        email: '',
      });
    }
  };

  const handleSubmit = () => {
    // Reset errors
    setErrors({});

    // Submit the form
    forgotPasswordMutation.mutate({ email });
  };

  // Show success screen
  if (isSuccess) {
    return (
      <ScrollView style={styles.container}>
        <View style={styles.successContainer}>
          <Text style={styles.successTitle}>Check Your Email</Text>
          <Text style={styles.successText}>
            We've sent a password reset link to {email}. Please check your email and follow the
            instructions to reset your password.
          </Text>
          <Text style={styles.successNote}>
            If you don't see the email in your inbox, please check your spam folder.
          </Text>

          <TouchableOpacity
            style={styles.returnButton}
            onPress={() => navigation.navigate('Login' as never)}
          >
            <Text style={styles.returnButtonText}>Return to Login</Text>
          </TouchableOpacity>
        </View>
      </ScrollView>
    );
  }

  return (
    <ScrollView style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.title}>Forgot Password</Text>
      </View>

      <View style={styles.formContainer}>
        <Text style={styles.instructions}>
          Enter your email address below and we'll send you a link to reset your password.
        </Text>

        <View style={styles.fieldContainer}>
          <Text style={styles.label}>Email Address</Text>
          <TextInput
            style={styles.input}
            value={email}
            onChangeText={handleInputChange}
            placeholder="Enter your email"
            keyboardType="email-address"
            autoCapitalize="none"
            autoComplete="email"
          />
          {errors.email && (
            <Text style={styles.errorText}>{errors.email}</Text>
          )}
        </View>

        <TouchableOpacity
          style={styles.submitButton}
          onPress={handleSubmit}
          disabled={forgotPasswordMutation.isPending}
        >
          <Text style={styles.submitButtonText}>
            {forgotPasswordMutation.isPending ? 'Sending...' : 'Send Reset Link'}
          </Text>
        </TouchableOpacity>

        <TouchableOpacity
          style={styles.returnToLoginButton}
          onPress={() => navigation.navigate('Login' as never)}
        >
          <Text style={styles.returnToLoginButtonText}>Return to Login</Text>
        </TouchableOpacity>
      </View>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f8f9fa',
  },
  header: {
    padding: 16,
    backgroundColor: '#ffffff',
    borderBottomWidth: 1,
    borderBottomColor: '#e1e4e8',
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
    color: '#333333',
  },
  formContainer: {
    padding: 16,
  },
  instructions: {
    fontSize: 16,
    lineHeight: 24,
    color: '#4a5568',
    marginBottom: 16,
  },
  fieldContainer: {
    marginBottom: 16,
  },
  label: {
    fontSize: 16,
    fontWeight: '500',
    marginBottom: 8,
    color: '#4a5568',
  },
  input: {
    backgroundColor: '#ffffff',
    borderWidth: 1,
    borderColor: '#e1e4e8',
    borderRadius: 8,
    padding: Platform.OS === 'ios' ? 12 : 10,
    fontSize: 16,
    color: '#333333',
  },
  errorText: {
    color: '#e53e3e',
    marginTop: 4,
    fontSize: 14,
  },
  submitButton: {
    backgroundColor: '#4299e1',
    borderRadius: 8,
    padding: 16,
    alignItems: 'center',
    marginTop: 16,
  },
  submitButtonText: {
    color: '#ffffff',
    fontSize: 16,
    fontWeight: '600',
  },
  returnToLoginButton: {
    padding: 16,
    alignItems: 'center',
    marginTop: 8,
  },
  returnToLoginButtonText: {
    color: '#4299e1',
    fontSize: 16,
    fontWeight: '500',
  },
  successContainer: {
    padding: 24,
    alignItems: 'center',
    justifyContent: 'center',
    minHeight: 400,
  },
  successTitle: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#38a169',
    marginBottom: 16,
    textAlign: 'center',
  },
  successText: {
    fontSize: 16,
    lineHeight: 24,
    color: '#4a5568',
    textAlign: 'center',
    marginBottom: 16,
  },
  successNote: {
    fontSize: 14,
    color: '#718096',
    textAlign: 'center',
    marginBottom: 24,
  },
  returnButton: {
    backgroundColor: '#4299e1',
    borderRadius: 8,
    padding: 16,
    alignItems: 'center',
    width: '80%',
    marginTop: 16,
  },
  returnButtonText: {
    color: '#ffffff',
    fontSize: 16,
    fontWeight: '600',
  },
});
